import React, {Component} from "react";
import { useSelector } from 'react-redux'
import "../styles/user-form.css";

class UserProfileComponent2 extends Component { 
  name = useSelector(state => state.userInfo.name);
  email = useSelector(state => state.userInfo.email);

  render() {
    function UserDataElement(props) {
      return (
        <div>
          <div>User Name: {props.name}</div>
          <div>User Email: {props.email}</div>
        </div>
      )
    }
    function DefaultElement() {
      return (
        <div>User Data Not Found. Please Submit use info in the form above.</div>
      )
    }

    function ShouldDisplayInfo(name, email) {
      return name.length > 0 && email.length > 0;
    }
    const AssessDisplayElement = () => {
      return this.ShouldDisplayInfo(this.name, this,email)?
        <UserDataElement 
          name={this.name}
          email={this.email}/>
        : <DefaultElement/>;
    }

    return (<AssessDisplayElement/>)
  }
}

export default UserProfileComponent2;
